sp_widget.client_script:
api.controller = function() {
	var c = this;
	c.add = function(){
		c.data.action = "add"
		c.server.update().then(function(){
			c.data.action=""
		})
	}

	c.show = function() {
		c.data.action = "show"
		c.server.update().then(function(res) {
			//c.data.lists = res.data.lists'
			c.data.action=""
		})
	}
}

