sp_widget.script:
(function(){
	var gr = new GlideRecord("u_products");
	if(input){
		if(input.action == "add"){
			gr.initialize();
			gr.setValue("u_product_name", input.pname);
			gr.setValue("u_product_brand", input.pbrand);
			gr.setValue("u_product_description", input.pdesc);
			gr.setValue("u_product_type", input.ptype);
			gr.setValue("u_product_quantity", input.pqty);
			gr.setValue("u_product_price", input.pprice);
			gr.insert();
		}

		if(input.action=="show"){
			data.lists=[]
			//var gr2 = new GlideRecord("u_products")
			gr.query()
			while(gr.next()){
				var obj = {
					 name: gr.u_product_name.toString(),
					 type: gr.u_product_type.toString(),
					 desc: gr.u_product_description.toString(),
					 brand: gr.u_product_brand.toString(),
					 quantity: gr.u_product_quantity.toString(),
					 price: gr.u_product_price.toString(),
				}
				data.lists.push(obj);

			}
			//gs.info("resilt"+lists);

		}
	}

})();
